<?php


namespace TGL\MapGen;


class SplitMap
{


    public static function split($map)
    {
        $csv = "";
        if($map === null)

            //tgl normal map
            //$map = "";

            //tgl secret map
            $map = "";
        for($y = 0; $y<=23; $y++)
        {
            for($x = 0; $x<=23; $x++)
            {
                $length = substr($map,1,1);
                $value = hexdec($length);

                $truelength = $value * 2 + 2;

                $csv.= "\"".substr($map,0,$truelength)."\",";

                $map = substr($map, $truelength, strlen($map));


            }
            $csv.= "\n";
        }

        return $csv;
    }
}